(25, 'Hotel Invoice', 'Hotel Invoice from %invoiceToEmail%', 'invoice_template', '<table width="100%"  border="0" cellspacing="1" cellpadding="10" align="center" bgcolor="#cccccc">\r\n<tbody>\r\n<tr>\r\n<td bgcolor="#ffffff">\r\n<table style="width: 100%;" border="0">\r\n<tbody>\r\n<tr>\r\n<td width="50%">\r\n<p><img src="%invoice_logo_url%" alt="" /></p>\r\n</td>\r\n<td width="50%" align="center"><strong><font class="unpaid">%payment_status%</font></strong><br /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<br /> \r\n<table style="width: 100%;" border="0" cellspacing="0">\r\n<tbody>\r\n<tr>\r\n<td style="border:1px solid #cccccc" width="50%">\r\n<table  style="width: 100%; height: 100px;" border="0" cellspacing="0" cellpadding="10">\r\n<tbody>\r\n<tr>\r\n<td  style="border:1px solid #cccccc" valign="top"><strong>Invoiced To</strong><br />%invoiceTo%</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n<td id="invoicecontent" style="border:1px solid #cccccc;border-left:0px;" width="50%">\r\n<table id="invoicetoptables" style="width: 100%; height: 100px;" border="0" cellspacing="0" cellpadding="10">\r\n<tbody>\r\n<tr>\r\n<td  style="border:1px solid #cccccc" valign="top"><strong>Pay To</strong><br /> \r\n%payTo% \r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong>Invoice #%invoice_id%</strong><br /> Invoice Date: %invoice_date%</p>\r\n%invoice_table%<br /><br /></td>\r\n</tr>\r\n</tbody>\r\n</table>', '2012-06-19 11:10:51'),
(29, 'Request for quote', 'Request for quote', '0', 'Dear </strong><strong>%company_name%</strong>,<br /> \r\n  Thanks for requesting quote to  <strong>%site_name%</strong>. We are happy to get you in our\r\n  team. We will check and consider your quote and send you our response as early\r\n  as possible<br />\r\n  <span style="text-decoration: underline;"><strong><br /> \r\n  Your Information :</strong></span><br />\r\n  <br /> \r\n  <strong>Compay Name:</strong> <strong>%company_name%</strong><br /> \r\n  <strong>Existing Client  :</strong> %existing_account_holder%<br /> \r\n  <strong>Contact Name  :</strong> %contact_name%<br /> \r\n  <strong>Phone number    :</strong> %phone_number%<br />\r\n<strong>Fax number :</strong> %fax_number%<br />\r\n<strong>Notify me by :</strong> %notify_by%<br />\r\n<strong>E-mail address :</strong> %email_address%</p>\r\n<hr>\r\n  <strong>Commodity :</strong> %commodity%<br />\r\n  <strong>Place of origin :</strong> %place_of_origin%<br />\r\n  <strong>Port of Origin :</strong> %port_of_origin%<br />\r\n  <strong>Destination :</strong> %destination%<br />\r\n<strong>Mode :</strong> %mode%</p>\r\n<hr>\r\n  <strong>Inco Terms :</strong> %inco_terms%<br />\r\n  <strong>Weight (Kgs) :</strong> %weight_kg%<br />\r\n  <strong>Weight (Cubic meters) :</strong> %weight_cubic%<br />\r\n  <strong>Size/Type :</strong> %size_type%<br />\r\n  <strong>Additional Comments :</strong> %comments%<br />\r\n  <br />\r\n  <br />\r\n  For any support with respect to your quotation or other issue with us you can\r\n  always contact us directly using the following Information.<br />\r\n  <br /> \r\n  <strong>Kind Regards</strong>,<br />\r\n  <br />\r\n  %site_name%<br />\r\n%site_url%<br />\r\n\r\n', '2012-01-10 12:28:09'),
(31, 'Order Confirmation', 'Order Confirmation', 'invoice_template', '<table style=\\"width: 100%;\\" border=\\"0\\" cellspacing=\\"1\\" cellpadding=\\"10\\" align=\\"center\\" bgcolor=\\"#cccccc\\">\n<tbody>\n<tr>\n<td bgcolor=\\"#ffffff\\">\n<table style=\\"width: 100%;\\" border=\\"0\\">\n<tbody>\n<tr>\n<td width=\\"50%\\">\n<p><img src=\\"%invoice_logo_url%\\" alt=\\"\\" /></p>\n</td>\n<td align=\\"center\\" width=\\"50%\\">\n<h2>%payment_status%</h2>\n</td>\n</tr>\n</tbody>\n</table>\n<br />\n<table style=\\"width: 100%;\\" border=\\"0\\" cellspacing=\\"0\\">\n<tbody>\n<tr>\n<td style=\\"border: 1px solid #cccccc;\\" width=\\"50%\\">\n<table style=\\"width: 100%; height: 100px;\\" border=\\"0\\" cellspacing=\\"0\\" cellpadding=\\"10\\">\n<tbody>\n<tr>\n<td style=\\"border: 1px solid #cccccc;\\" valign=\\"top\\"><strong>Invoiced To</strong><br />%invoiceTo%</td>\n</tr>\n</tbody>\n</table>\n</td>\n<td id=\\"invoicecontent\\" style=\\"border: 1px solid #cccccc; border-left: 0px;\\" width=\\"50%\\">\n<table id=\\"invoicetoptables\\" style=\\"width: 100%; height: 100px;\\" border=\\"0\\" cellspacing=\\"0\\" cellpadding=\\"10\\">\n<tbody>\n<tr>\n<td style=\\"border: 1px solid #cccccc;\\" valign=\\"top\\"><strong>Pay To</strong><br /> %payTo%</td>\n</tr>\n</tbody>\n</table>\n</td>\n</tr>\n</tbody>\n</table>\n<p><strong>Dear&nbsp;</strong>%title% %firstName% %lastName%,&nbsp;<br /><br />We have received your order and will be processing it shortly. The details of the order are below:<strong><br /><br />Invoice #%invoice_id%</strong><br /> Invoice Date: %invoice_date%<br /><br /></p>\n<p>&nbsp;</p>\n%invoice_table%<br /><br />\n<p><br />You will receive an email from us shortly once your account has been setup. Please quote your order reference number if you wish to contact us about this order.&nbsp;<br />&nbsp;</p>\n<p><br /><strong>Kind Regards</strong>,<br /><br />Service Team<br />%site_name%<br />%site_url%</p>\n</td>\n</tr>\n</tbody>\n</table>', '2012-07-13 08:40:19'),
(32, 'Invoice Payment Reminder', 'Invoice Payment Reminder', 'invoice_template', '<div class="invoice" style="border:1px solid #E1E1E1; font-size:12px; font-family:Arial, Helvetica, sans-serif; padding:5px;">\r\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="10" align="center">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellpadding="0" cellspacing="0" style="width: 100%;">\r\n<tbody>\r\n<tr>\r\n<td width="50%">\r\n<p><img src="%invoice_logo_url%" alt="" /></p>\r\n</td>\r\n<td align="right" width="50%">\r\n<h1>%payment_status%</h1>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table></td>\r\n</tr>\r\n<tr>\r\n  <td><br /><table border="0" cellpadding="0" cellspacing="0" style="width: 100%;">\r\n    <tbody>\r\n      <tr>\r\n        <td width="50%"><table width="98%" height="100" border="0" cellspacing="0" cellpadding="10" class="invoice-to">\r\n          <tbody>\r\n            <tr>\r\n              <td valign="top" style="border:2px solid #D3EE9D; background-color:#F2FAE2; padding:5px;"><p style="line-height:28px; margin:0; padding:0; font-weight:bold;">Invoiced To</p>\r\n                %invoiceTo%</td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n        <td id="invoicecontent" width="50%"><table width="98%" height="100" border="0" align="right" cellpadding="10" cellspacing="0" class="pay-to" id="invoicetoptables">\r\n          <tbody>\r\n            <tr>\r\n              <td valign="top" style="border:2px solid #FFE89F; background-color:#FFFFDD; padding:5px;"><p style="line-height:28px; margin:0; padding:0; font-weight:bold;">Pay To</p>\r\n                %payTo%</td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n      </tr>\r\n    </tbody>\r\n  </table><br />\r\n  </td>\r\n</tr>\r\n\r\n<tr>\r\n  <td>\r\n<p style="line-height:28px; margin:0; padding:0; font-weight:bold;">DearÂ </strong>%title% %firstName% %lastName%,Â </p>\r\nThis is a courtesy note to inform you that, following invoice is remain pending for payment.<br /><br /><br />\r\n<table width="100%" height="50" border="0" cellspacing="0" cellpadding="10" bgcolor="#F3F3F3" class="invoice-id">\r\n<tr>\r\n  <td style="border:1px solid #AAAAAA; border-style:dashed; font-size:16px; line-height:14px; margin:0; padding:10px; font-weight:bold;"><p>Invoice Number : %invoice_id%</p><p>Invoice Date : %invoice_date%</p>\r\n  <p>Total Amount : %invoice_amount%</p></td>\r\n</tr>\r\n</table>\r\n<br />\r\n<br /><br />\r\n<div style="font-weight:bold;">You can login to your member account to re-view and pay at your connivances.</div><br /><br />%invoice_table%<br /><br /><br />You will receive an email from us shortly once your account has been setup. Please quote your order reference number if you wish to contact us about this order.<br /><br />\r\n\r\nIt is our immense pleasure to have you with us.<br /><br />\r\n\r\n<p style="line-height:28px; margin:0; padding:0; font-weight:bold;">Kind Regards,</p>Service Team<br />%site_name%<br />%site_url%</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>', '2012-11-27 10:41:35'),
(33, 'Invoice Payment Confirmation', 'Invoice Payment Confirmation', 'invoice_template', '<div class="invoice" style="border:1px solid #E1E1E1; font-size:12px; font-family:Arial, Helvetica, sans-serif; padding:5px;">\r\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="10" align="center">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellpadding="0" cellspacing="0" style="width: 100%;">\r\n<tbody>\r\n<tr>\r\n<td width="50%">\r\n<p><img src="%invoice_logo_url%" alt="" /></p>\r\n</td>\r\n<td align="right" width="50%">\r\n<h1>%payment_status%</h1>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table></td>\r\n</tr>\r\n<tr>\r\n  <td><br /><table border="0" cellpadding="0" cellspacing="0" style="width: 100%;">\r\n    <tbody>\r\n      <tr>\r\n        <td width="50%"><table width="98%" height="100" border="0" cellspacing="0" cellpadding="10" class="invoice-to">\r\n          <tbody>\r\n            <tr>\r\n              <td valign="top" style="border:2px solid #D3EE9D; background-color:#F2FAE2; padding:5px;"><p style="line-height:28px; margin:0; padding:0; font-weight:bold;">Invoiced To</p>\r\n                %invoiceTo%</td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n        <td id="invoicecontent" width="50%"><table width="98%" height="100" border="0" align="right" cellpadding="10" cellspacing="0" class="pay-to" id="invoicetoptables">\r\n          <tbody>\r\n            <tr>\r\n              <td valign="top" style="border:2px solid #FFE89F; background-color:#FFFFDD; padding:5px;"><p style="line-height:28px; margin:0; padding:0; font-weight:bold;">Pay To</p>\r\n                %payTo%</td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n      </tr>\r\n    </tbody>\r\n  </table><br />\r\n  </td>\r\n</tr>\r\n\r\n<tr>\r\n  <td>\r\n<p style="line-height:28px; margin:0; padding:0; font-weight:bold;">DearÂ </strong>%title% %firstName% %lastName%,Â </p>\r\nThanks for ordering with us. This is a payment receipt for Invoice # %invoice_id% sent on %invoice_date% .  Please download and get in printed to preserve as a hard copy for future perusal.\r\n<br /><br /><br />\r\n<table width="100%" height="50" border="0" cellspacing="0" cellpadding="10" bgcolor="#F3F3F3" class="invoice-id">\r\n<tr>\r\n  <td style="border:1px solid #AAAAAA; border-style:dashed; font-size:16px; line-height:14px; margin:0; padding:10px; font-weight:bold;"><p>Invoice Number : %invoice_id%</p><p>Invoice Date : %invoice_date%</p>\r\n  <p>Total Amount : %invoice_amount%</p></td>\r\n</tr>\r\n</table>\r\n<br />\r\n<br /><br />\r\n<div style="font-weight:bold; text-decoration:underline">You may review your invoice history at any time by logging in to your client area. Here is a short detail as follows:</div><br /><br />%invoice_table%<br /><br /><br />This email will serve as an official receipt for this payment. If you have any inquires regarding this invoice. Please feel free to contact with our billing department.<br /><br />\r\n\r\nIt is our immense pleasure to have you with us.<br /><br />\r\n\r\n<p style="line-height:28px; margin:0; padding:0; font-weight:bold;">Kind Regards,</p>Billing Team<br />%site_name%<br />%site_url%</p>\r\n  </td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>', '2012-11-27 10:41:38'),
(34, 'Invoice Created', 'Invoice Created', 'invoice_template', '<div class="invoice" style="border:1px solid #E1E1E1; font-size:12px; font-family:Arial, Helvetica, sans-serif; padding:5px;">\r\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="10" align="center">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellpadding="0" cellspacing="0" style="width: 100%;">\r\n<tbody>\r\n<tr>\r\n<td width="50%">\r\n<p><img src="%invoice_logo_url%" alt="" /></p>\r\n</td>\r\n<td align="right" width="50%">\r\n<h1>%payment_status%</h1>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table></td>\r\n</tr>\r\n<tr>\r\n  <td><br /><table border="0" cellpadding="0" cellspacing="0" style="width: 100%;">\r\n    <tbody>\r\n      <tr>\r\n        <td width="50%"><table width="98%" height="100" border="0" cellspacing="0" cellpadding="10" class="invoice-to">\r\n          <tbody>\r\n            <tr>\r\n              <td valign="top" style="border:2px solid #D3EE9D; background-color:#F2FAE2; padding:5px;"><p style="line-height:28px; margin:0; padding:0; font-weight:bold;">Invoiced To</p>\r\n                %invoiceTo%</td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n        <td id="invoicecontent" width="50%"><table width="98%" height="100" border="0" align="right" cellpadding="10" cellspacing="0" class="pay-to" id="invoicetoptables">\r\n          <tbody>\r\n            <tr>\r\n              <td valign="top" style="border:2px solid #FFE89F; background-color:#FFFFDD; padding:5px;"><p style="line-height:28px; margin:0; padding:0; font-weight:bold;">Pay To</p>\r\n                %payTo%</td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n      </tr>\r\n    </tbody>\r\n  </table><br /></td>\r\n</tr>\r\n\r\n<tr>\r\n  <td>\r\n<p style="line-height:28px; margin:0; padding:0; font-weight:bold;">DearÂ </strong>%title% %firstName% %lastName%,Â </p>\r\nThanks for ordering with us. This is a courtesy email to inform you that, anÂ Invoice # %invoice_id% has been generated onÂ on %invoice_date% . Â Please download and get in printed to preserve as a hard copy for future perusal.\r\n<br /><br /><br />\r\n<table width="100%" height="50" border="0" cellspacing="0" cellpadding="10" bgcolor="#F3F3F3" class="invoice-id">\r\n<tr>\r\n  <td style="border:1px solid #AAAAAA; border-style:dashed; font-size:16px; line-height:14px; margin:0; padding:10px; font-weight:bold;"><p>Invoice Number : %invoice_id%</p><p>Invoice Date : %invoice_date%</p>\r\n  <p>Total Amount : %invoice_amount%</p></td>\r\n</tr>\r\n</table>\r\n<br /><br />\r\n<div style="font-weight:bold; text-decoration:underline">You may review your invoice history at any time by logging in to your client area. Here is a short detail as follows:</div><br /><br />%invoice_table%<br /><br /><br />You can login to your client area to view and pay the invoice.Â It is our immense pleasure to have you with us.<br /><br />\r\n\r\n<p style="line-height:28px; margin:0; padding:0; font-weight:bold;">Kind Regards,</p>Billing Team<br />%site_name%<br />%site_url%</p>\r\n  </td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>', '2012-11-27 10:41:45');
