(35, 'Invoice Overdue Notice', 'Invoice Overdue Notice', 'invoice_template', '<div class="invoice" style="border:1px solid #E1E1E1; font-size:12px; font-family:Arial, Helvetica, sans-serif; padding:5px;">\r\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="10" align="center">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellpadding="0" cellspacing="0" style="width: 100%;">\r\n<tbody>\r\n<tr>\r\n<td width="50%">\r\n<p><img src="%invoice_logo_url%" alt="" /></p>\r\n</td>\r\n<td align="right" width="50%">\r\n<h1>%payment_status%</h1>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table></td>\r\n</tr>\r\n<tr>\r\n  <td><br /><table border="0" cellpadding="0" cellspacing="0" style="width: 100%;">\r\n    <tbody>\r\n      <tr>\r\n        <td width="50%"><table width="98%" height="100" border="0" cellspacing="0" cellpadding="10" class="invoice-to">\r\n          <tbody>\r\n            <tr>\r\n              <td valign="top" style="border:2px solid #D3EE9D; background-color:#F2FAE2; padding:5px;"><p style="line-height:28px; margin:0; padding:0; font-weight:bold;">Invoiced To</p>\r\n                %invoiceTo%</td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n        <td id="invoicecontent" width="50%"><table width="98%" height="100" border="0" align="right" cellpadding="10" cellspacing="0" class="pay-to" id="invoicetoptables">\r\n          <tbody>\r\n            <tr>\r\n              <td valign="top" style="border:2px solid #FFE89F; background-color:#FFFFDD; padding:5px;"><p style="line-height:28px; margin:0; padding:0; font-weight:bold;">Pay To</p>\r\n                %payTo%</td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n      </tr>\r\n    </tbody>\r\n  </table><br />\r\n  </td>\r\n</tr>\r\n\r\n<tr>\r\n  <td>\r\n<p style="line-height:28px; margin:0; padding:0; font-weight:bold;">DearÂ </strong>%title% %firstName% %lastName%,Â </p><br />\r\nThanks for ordering with us. This is a courtesy email to inform you that, an Invoice # %invoice_id% has been generated on on %invoice_date%is now overdue. Failure to make payment will result in account suspension.<br /><br /><br />\r\n<table width="100%" height="50" border="0" cellspacing="0" cellpadding="10" bgcolor="#F3F3F3" class="invoice-id">\r\n<tr>\r\n  <td style="border:1px solid #AAAAAA; border-style:dashed; font-size:16px; line-height:14px; margin:0; padding:10px; font-weight:bold;"><p>Invoice Number : %invoice_id%</p><p>Invoice Date : %invoice_date%</p>\r\n  <p>Total Amount : %invoice_amount%</p></td>\r\n</tr>\r\n</table>\r\n<br />\r\n<br /><br />\r\n<div style="font-weight:bold; text-decoration:underline">You may review the invoice history and make payment at any time by logging in to your client area. Here is a short detail as follows:</div><br /><br />%invoice_table%<br /><br /><br />Please consider this email urgent nofication of payment and we are looking forward to hear from you. It is our immense pleasure to have you with us.<br />\r\n<br />\r\n<br />\r\n\r\n<p style="line-height:28px; margin:0; padding:0; font-weight:bold;">Kind Regards,</p>Billing Team<br />%site_name%<br />%site_url%</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>', '2012-11-27 10:41:40'),
(36, 'Invoice Cancellation Notice', 'Invoice Cancellation Notice', 'invoice_template', '<div class="invoice" style="border:1px solid #E1E1E1; font-size:12px; font-family:Arial, Helvetica, sans-serif; padding:5px;">\r\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="10" align="center">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellpadding="0" cellspacing="0" style="width: 100%;">\r\n<tbody>\r\n<tr>\r\n<td width="50%">\r\n<p><img src="%invoice_logo_url%" alt="" /></p>\r\n</td>\r\n<td align="right" width="50%">\r\n<h1>%payment_status%</h1>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table></td>\r\n</tr>\r\n<tr>\r\n  <td><br /><table border="0" cellpadding="0" cellspacing="0" style="width: 100%;">\r\n    <tbody>\r\n      <tr>\r\n        <td width="50%"><table width="98%" height="100" border="0" cellspacing="0" cellpadding="10" class="invoice-to">\r\n          <tbody>\r\n            <tr>\r\n              <td valign="top" style="border:2px solid #D3EE9D; background-color:#F2FAE2; padding:5px;"><p style="line-height:28px; margin:0; padding:0; font-weight:bold;">Invoiced To</p>\r\n                %invoiceTo%</td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n        <td id="invoicecontent" width="50%"><table width="98%" height="100" border="0" align="right" cellpadding="10" cellspacing="0" class="pay-to" id="invoicetoptables">\r\n          <tbody>\r\n            <tr>\r\n              <td valign="top" style="border:2px solid #FFE89F; background-color:#FFFFDD; padding:5px;"><p style="line-height:28px; margin:0; padding:0; font-weight:bold;">Pay To</p>\r\n                %payTo%</td>\r\n            </tr>\r\n          </tbody>\r\n        </table></td>\r\n      </tr>\r\n    </tbody>\r\n  </table><br />\r\n  </td>\r\n</tr>\r\n\r\n<tr>\r\n  <td>\r\n<p style="line-height:28px; margin:0; padding:0; font-weight:bold;">DearÂ </strong>%title% %firstName% %lastName%,Â </p>\r\nWe are sorry to inform you that the following invoice has been cancell